import React from "react";
import { useNavigate } from 'react-router-dom';
import {useState} from 'react';
import { supabase } from "../client";

const AddDesigner = async({addDesigner}) => {
    const [post, setPost] = useState({name: "", style: "", bio_summary: ""});
    const navigate = useNavigate();

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost((prev) => {
            return {
                ...prev,
                [name]: value,
            };
        });
    };

    const handleSubmit = async(event) => {
        event.preventDefault();

        await supabase
            .from('Designers')
            .insert({name:post.name, style:post.style, bio_summary:post.bio_summary})
            .select();

            addDesigner({ ...post, id: Date.now().toString() });
            navigate("/designer-gallery");


        window.location = "/";
    };


    return (
        <div>
            <h2>Add a Designer</h2>
            {/**my form content */}
            <div>
                <form onSubmit={han}>
                    <label htmlFor="name">Name</label> <br />
                    <input type="text" id="name" onChange={handleChange} /><br />
                    <br />

                    <label htmlFor="style">Style</label><br />
                    <input type="text" id="author" name="author" onChange={handleChange} /><br />
                    <br/>

                    <label htmlFor="bio_summary">Bio-summary</label><br />
                    <textarea rows="5" cols="50" id="bio_summary" name="bio_summary" onChange={handleChange}></textarea>
                    <br />

                    <input type="submit" value="Submit" onClick={addDesigner}/>
                </form>
            </div>
        </div>
    );
};

export default AddDesigner;
