import React from "react";
import {useState} from 'react';
import { supabase } from "../client";

const AddDesigner = () => {
    const [post, setPost] = useState({name: "", style: "", bio_summary: ""});

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost((prev) => {
            return {
                ...prev,
                [name]: value,
            };
        });
    };

    const addDesigner = async(event) => {
        event.preventDefault();

        await supabase
            .from('Designers')
            .insert({name:post.name, style:post.style, bio_summary:post.bio_summary})
            .select();
        window.location = "/";
    }


    return (
        <div>
            <h2>Add a Designer</h2>
            {/**my form content */}
            <div>
                <form>
                    <label for="name">Name</label> <br />
                    <input type="text" id="name" onChange={handleChange} /><br />
                    <br />

                    <label for="style">Style</label><br />
                    <input type="text" id="author" name="author" onChange={handleChange} /><br />
                    <br/>

                    <label for="bio_summary">Bio-summary</label><br />
                    <textarea rows="5" cols="50" id="bio_summary" name="bio_summary" onChange={handleChange}></textarea>
                    <br />

                    <input type="submit" value="Submit" onClick={addDesigner}/>
                </form>
            </div>
        </div>
    );
};

export default AddDesigner;
